INSERT INTO `wp_postmeta` VALUES (406,41,'encontros_1_objetivos','<ul>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Definir proposta de regionalização alternativa do continente americano, com base nas informações disponíveis na base de dados; construir visualizações que ilustrem a proposta de regionalização.</span></li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (407,41,'_encontros_1_objetivos','field_67918a5e6fea3');
INSERT INTO `wp_postmeta` VALUES (408,41,'encontros_1_principais_etapas','<h3><b>1) Proposta de regionalização [10 min]</b></h3>\r\n<span style=\"font-weight: 400;\">Retome as etapas do encontro anterior e o conceito de regionalização no contexto do continente americano. Em seguida, oriente os/as alunos/as a descreverem ao menos uma forma alternativa de regionalização que gostariam de explorar a partir dos dados disponíveis, conforme </span><b>cartão de atividades 2</b><span style=\"font-weight: 400;\"> e com o apoio da </span><b>base de dados do continente americano</b><span style=\"font-weight: 400;\">.</span>\r\n\r\n&nbsp;\r\n<h3><b>2) Programação de regionalizações [60 min]</b></h3>\r\n<span style=\"font-weight: 400;\">Em grupos, estudantes programam visualizações dinâmicas para as possíveis regionalizações das Américas, com base na proposta definida na etapa anterior e com apoio do </span><b>cartão de atividades 3</b><span style=\"font-weight: 400;\"> e </span><b>cartão de recursos 2</b><span style=\"font-weight: 400;\">.</span>\r\n\r\n<span style=\"font-weight: 400;\">Ao final do encontro, os grupos fazem o download de seus projetos para poder continuá-los no encontro seguinte.</span>');
INSERT INTO `wp_postmeta` VALUES (409,41,'_encontros_1_principais_etapas','field_67918c326fea5');
INSERT INTO `wp_postmeta` VALUES (410,41,'encontros_1_materiais_e_ferramentas','<ul>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Computadores com acesso a internet</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Google Planilhas</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Scratch com extensão do PlayData</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/document/d/1f-wuV3EP14eIdqydoN5Xcnif8gl49XgejRMBxsywP5E/edit?tab=t.l8u0ynzatzis#heading=h.byogzsm5cxy0\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Cartões de atividades 2 e 3</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/presentation/d/1CmRjJJeALN6wHbeBGX7QP5Adz9dFjdcAXdqsahK9mCU/edit#slide=id.p\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Cartão de recursos 2</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/spreadsheets/u/1/d/18yHEs-TLKXCUcxpFnVlxWeQ1N6KCBVJB23Pj9Z-2CT4/edit?gid=1185543801#gid=1185543801\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Base de dados - continente americano</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/document/d/1hyRxuw-aIqeSk3QilOoh1xPwpe3myqqZtlDRSakzrGo/edit#heading=h.5ngsnj70246r\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Guia de preparação do encontro</span></a></li>\r\n</ul>');
