INSERT INTO `wp_postmeta` VALUES (406,41,'encontros_1_objetivos','<h3><span style=\"font-weight: 400;\">Objetivos:</span></h3>\r\n<ul>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Definir proposta de regionalização alternativa do continente americano, com base nas informações disponíveis na base de dados; construir visualizações que ilustrem a proposta de regionalização.</span></li>\r\n</ul>');
INSERT INTO `wp_postmeta` VALUES (407,41,'_encontros_1_objetivos','field_67918a5e6fea3');
INSERT INTO `wp_postmeta` VALUES (408,41,'encontros_1_principais_etapas','<h3><span style=\"font-weight: 400;\">Principais etapas:</span></h3>\r\n<b>1) Proposta de regionalização [10 min]</b>\r\n\r\n<span style=\"font-weight: 400;\">Retome as etapas do encontro anterior e o conceito de regionalização no contexto do continente americano. Em seguida, oriente os/as alunos/as a descreverem ao menos uma forma alternativa de regionalização que gostariam de explorar a partir dos dados disponíveis, conforme </span><b>cartão de atividades 2</b><span style=\"font-weight: 400;\"> e com o apoio da </span><b>base de dados do continente americano</b><span style=\"font-weight: 400;\">.</span>\r\n\r\n&nbsp;\r\n\r\n<b>2) Programação de regionalizações [60 min]</b>\r\n\r\n<span style=\"font-weight: 400;\">Em grupos, estudantes programam visualizações dinâmicas para as possíveis regionalizações das Américas, com base na proposta definida na etapa anterior e com apoio do </span><b>cartão de atividades 3</b><span style=\"font-weight: 400;\"> e </span><b>cartão de recursos 2</b><span style=\"font-weight: 400;\">.</span>\r\n\r\n<span style=\"font-weight: 400;\">Ao final do encontro, os grupos fazem o download de seus projetos para poder continuá-los no encontro seguinte.</span>');
