INSERT INTO `wp_postmeta` VALUES (410,41,'encontros_1_materiais_e_ferramentas','<h3><span style=\"font-weight: 400;\">Materiais</span></h3>\r\n<ul>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Computadores com acesso a internet</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Google Planilhas</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><span style=\"font-weight: 400;\">Scratch com extensão do PlayData</span></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/document/d/1f-wuV3EP14eIdqydoN5Xcnif8gl49XgejRMBxsywP5E/edit?tab=t.l8u0ynzatzis#heading=h.byogzsm5cxy0\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Cartões de atividades 2 e 3</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/presentation/d/1CmRjJJeALN6wHbeBGX7QP5Adz9dFjdcAXdqsahK9mCU/edit#slide=id.p\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Cartão de recursos 2</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/spreadsheets/u/1/d/18yHEs-TLKXCUcxpFnVlxWeQ1N6KCBVJB23Pj9Z-2CT4/edit?gid=1185543801#gid=1185543801\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Base de dados - continente americano</span></a></li>\r\n 	<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://docs.google.com/document/d/1hyRxuw-aIqeSk3QilOoh1xPwpe3myqqZtlDRSakzrGo/edit#heading=h.5ngsnj70246r\" target=\"_blank\" rel=\"noopener\"><span style=\"font-weight: 400;\">Guia de preparação do encontro</span></a></li>\r\n</ul>');
